{ 
    Node *t1, *t2; *j=0; 
    t1 = head; 
    if (t1! = NULL) 
        t2 = t1 ->next; 
    else return head;
    *j = 1; 
    if(t2 == NULL) return head; 
    while (t2 != NULL) 
    { 
        if (t1.val != t2.val) ----------------> (S1) 
        { 
            (*j)++; 
            t1 -> next = t2; 
            t1 = t2; -----> (S2) 
